 (function() { 'use strict'; const navToggle = document.querySelector('.nav-toggle'); const navMenu = document.querySelector('nav ul'); const navLinks = document.querySelectorAll('nav a'); if (navToggle) { navToggle.addEventListener('click', function() { navMenu.classList.toggle('open'); navToggle.setAttribute('aria-expanded', navMenu.classList.contains('open')); }); } navLinks.forEach(link => { link.addEventListener('click', function() { navMenu.classList.remove('open'); if (navToggle) { navToggle.setAttribute('aria-expanded', 'false'); } }); }); document.addEventListener('keydown', function(event) { if (event.key === 'Escape' && navMenu.classList.contains('open')) { navMenu.classList.remove('open'); if (navToggle) { navToggle.setAttribute('aria-expanded', 'false'); } } }); const currentPage = window.location.pathname.split('/').pop() || 'index.html'; navLinks.forEach(link => { const href = link.getAttribute('href'); if (href === '/' && currentPage === 'index.html') { link.classList.add('active'); } else if (href === currentPage || href.includes(currentPage)) { link.classList.add('active'); } }); const accordionHeaders = document.querySelectorAll('.accordion-header'); accordionHeaders.forEach(header => { header.addEventListener('click', function() { const item = this.parentElement; item.classList.toggle('open'); }); }); })(); 